package com.syntax.class3;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i=1;
	    while(i<=10){
	      
	
	    	
	        
	      if (i==7 ||i==8 ||i==9 ||i==5) {
	    	  i++;
	    	  continue;
	       
	      }
	      System.out.println(i);
	      i++;
	    }
	    
	}

}
